    private void eqActionPerformed(java.awt.event.ActionEvent evt) {                                   
        num2 = Double.parseDouble(display.getText());
        switch (operator) {
            case '+':
                output = num1 + num2;
                break;
            case '-':
                output = num1 - num2;
                break;
            case '*':
                output = num1 * num2;
                break;
            case '/':
                output = num1 / num2;
                break;
            default:
                break;

        }
        operator = '\0';
        display.setText("" + output);

    }                                  

