    private void onoffActionPerformed(java.awt.event.ActionEvent evt) {                                      
        if(onoff.getText() == "OFF"){
            onoff.setText("ON");
        }
        else {
            onoff.setText("OFF");
            display.setText("");
        }
    }                                     

